#!/usr/bin/env python3

#create dna variable
dna = ''
#create a new all upper variable
dna_upper = dna.upper()
#count A's 
dna_a = dna_upper.count('A')
#count T's
dna_t = dna_upper.count('T')
#count G's
dna_g = dna_upper.count('G')
#count C's
dna_c = dna_upper.count('C')
#print output
print(f'''the sequence contains
      {dna_a} A's
      {dna_t} T's
      {dna_g} G's
      {dna_c} C's''')
#calculate total content
total_nt = int(dna_a+dna_t+dna_c+dna_g)
#calculate percent AT
perc_at = int(dna_a+dna_t)/total_nt
#calculate percent GC
perc_gc = int(dna_g+dna_c)/total_nt
#print result
print(f'total AT content is {perc_at:.2%} and GC content is {perc_gc:.2%}')

