#!/usr/bin/env python3

#dna sequence stored
dna = ''
#find EcoRI sequence
ecori = 'GAATTC'
#start position
start = int(dna.find(ecori))+1
#end position
end = int(dna.find(ecori))+6
#print result
print(f'the EcoRI site is found at nucleotides {start} through {end}')

#find EcoRI sequence in opposite strand
complement = dna.replace('A','t').replace('T','a').replace('G','c').replace('C','g')
dna_comp = complement.upper()
dna_rev = dna_comp[::-1]

print(len(dna_rev))

rev_start = int(dna_rev.find(ecori))+1
rev_end = int(dna_rev.find(ecori))+6

print(f'the EcoRI on the opposite strand is at nt {rev_start} through {rev_end}')