#!/usr/bin/env python3

#dna sequence stored
dna = ''
#replace nt's with compliment
comp_code = dna.replace('A','1').replace('a','2').replace('T','3').replace('t','4').replace('G','5').replace('g','6').replace('C','7').replace('c','8')
comp = comp_code.replace('1','T').replace('2','t').replace('3','A').replace('4','a').replace('5','C').replace('6','c').replace('7','G').replace('8','g')
print(f'complement:{comp}')
#reverse the complement
reverse = comp[::-1]
print(f'reverse complement: {reverse}')
