#!/usr/bin/env python3

#define dna sequence
dna = ''
#ensure all upper case
dna_upper = dna.upper()
#extract nucleotide position 100-200
dna_cut = dna_upper[99:200]

#calculate A,C and G,T content
dna_at = dna_cut.count('A')+dna_cut.count('T')
dna_gc = dna_cut.count('G')+dna_cut.count('C')

#calculate total nt content
total_nt = len(dna_cut)

#calculate at and gc content
per_at = dna_at/total_nt
per_gc = dna_gc/total_nt

#print result
print(f'total AT content is {per_at:.2%} and GC content is {per_gc:.2%}')