#!/usr/bin/env python3

seq = ''
ecori = 'GAATTC'

#replace sequence with splicable character
seq_new = seq.replace(ecori,'G^AATTC')

#splice into two new fragments
split_list = seq_new.split('^')

#iterate over fragments and report start position and end position in initial sequence
for nt in split_list:
    print(f"start: {seq.find(nt)}")
    print(f"end: {seq.find(nt)+len(nt)-1}")
    print(f"length:{len(nt)}")
    print("\n")