#!/usr/bin/env python3

#looking at the difference in syntax between two sets
my_set = set('ATGTGGG')
my_set2 = {'ATGTGGG'}
print(f'lenght of first is {len(my_set)} but lenght of second is {len(my_set2)}')

#create two sets of numbers
set1 = {'3','14','15','9','26','5','35','9'}
set2 = {'60','22','14','0','9'}
#intersection of sets
print(f'intersection: {set1 & set2})')
#difference between sets
print(f'difference: {set1 - set2}')
#union of sets
print(f'union: {set1 | set2}')
#symmetrical difference between sets
print(f'symmetric difference: {set1 ^ set2}')

#create a DNA sequence set
dna = set('')
print(dna)